  function validateForm (){
    return usuario.length > 0 && senha.length > 0;
  };
  function handleSubmit(event) {
    event.preventDefault();
  }
  return (
    <LoginContainer>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label>Usu√°rio</Label>
        </FormGroup>
        <FormGroup >
          <Form.Label>Senha</Form.Label>
        </FormGroup>
        <LoginButton 
        block size="lg" 
        type="submit" 
        disabled={!validateForm()}
        >
          Login
        </LoginButton>
      </Form>
    </LoginContainer>
  );
};
