import styled from "styled-components";
import React from "react";

export const LoginButton = styled.button`
padding: 20px 40px;
margin: 25px;

`;
export const Form = styled.form`
color: palevioletred;
display: block;
width: 300px;
margin: 50px auto;
`;
export const LoginContainer = styled.div`
  display: flex;
  justify-content:center;
  align-items: center;
  width: 100%;
  height: 100%;
  background-color: #f9f9f9;
  position: absolute;
`;
export const Label = styled.label`
	margin-bottom: 0.5em;
	color: black;
  font-weight: bold;
  display: block;
`;

export const FormGroup = styled.div`
	color: grey;
  display: block;
	width: 300px;
	margin: 50px auto;
`;
export const Input = styled.input`
	padding: 0.5em;
	color: black;
	background: gray;
	border: 1px;
	border-radius: 3px;
	width: 100%;
	margin-bottom: 0.5em;
`;
