import React, { useState } from "react";
import { Form, LoginButton,LoginContainer} from "./styles";


const LoginPage = () => {
  const [usuario, setUsuario] = useState("");
  const [senha, setSenha] = useState("");
  function validateForm (){
    return usuario.length > 0 && senha.length > 0;
  };
  function handleSubmit(event) {
    event.preventDefault();
  }
  return (
    <LoginContainer>

      <Form onSubmit={handleSubmit}>
        <Form.Group size="lg" controlId="usuario">
          <Form.Label>Usu√°rio</Form.Label>
          <Form.Control 
          type="usuario"
          values={usuario}
          onChange={(e)=>setUsuario(e.target.values)}
          />
        </Form.Group>
        <Form.Group size="lg" controlId="senha">
          <Form.Label>Senha</Form.Label>
          <Form.Control
          type="senha"
          value={senha}
          onChange={(e)=>setSenha(e.target.values)}
          />
        </Form.Group>
        <LoginButton block size="lg" type="submit" disabled={!validateForm()}>
          Login
          </LoginButton>
      </Form>
    </LoginContainer>
  );
};
export default LoginPage;
